    return <div className="App m-5border border-dark rounded p-5">
        <form id="formulario">
        <h3>Sign Up</h3>
        <div className="mb-3">
          <label>Nombre</label>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            id="Nombre"
          />
        </div>
        <div className="mb-3">
          <label>Apellido</label>
          <input type="text" className="form-control" placeholder="Apellido"  id="Apellido"/>
        </div>
        <div className="mb-3">
          <label>Email </label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            id="email"
            onChange={e=> {if((e.target.value).includes("@")){
              setEmail(e.target.value);
