import React, { useState } from "react";
import { useUserContext } from "../../contexts/UserContext";
import "./CardClase.css";
import mock from "../../data/mock.json";

export default function CardClaseFilter({
  id,
  name,
  profesor1,
  duracion,
  frecuencia,
  costo,
  calificacion,
  comentarios,
  _alumnos,
}) {
  const { user, profesores, alumnos } = useUserContext();
  const clases = mock.clases;
  const [combobox, setCombobox] = useState(false);
  const [filterClase, setFilterClase] = useState({});

  const Combobox = (_id) => {
    
    return(<>
    <select name="select" id={''+_id}>
      <option value="value1">Aceptado</option>
      <option value="value2" selected>
        Pendiente
      </option>
      <option value="value3">Rechazado</option>
    </select>
    <button className="btn btn-success" >Guardar</button>
    </>
    )
  };
  const redirect = () => {
    setCombobox((current) => !current);
  };
  const controlComents = () => {
    return (
      <>
        <h3>Comentarios:</h3>
        {comentarios.map((comentario) => (
          <div className="text-start mx-2 border border-warning rounded">
            <p key={"Alumno" + comentario.id} className="p-1 my-0 fw-bold">
              Alumno: {comentario.alumno}
            </p>
            <hr className="m-1" />
            <p key={"comentario" + comentario.id} className="px-2 py-0 my-0">
              {comentario.comentario}
            </p>
            <div key={"estado" + comentario.id} className="px-2 py-0 my-0">
              Estado: {comentario.estado}
            </div>
            {combobox && <Combobox value={id}/>}
          </div>
        ))}
      </>
    );
  };

  if (user.role === "Profesor") {
    if (profesor1 == user.id) {
      return (
        <div key={id} className="card">
          <div className="container">
          <button className="btn btn-warning" onClick={redirect}>
              Modificar estado
            </button>
            <h4 className="display-6">
              <b>{name}</b>
            </h4>
            <div>
              <p className="m-0">Frecuencia: {frecuencia}</p>
              <p className="m-0">Duracion: {duracion}</p>
              <div>{controlComents()}</div>
            </div>
          </div>
        </div>
      );
    }
  } else {
  }
}
