import React, { useContext, useState } from "react";
import mock from "../../data/mock.json";
import { UserContext } from "../../contexts/UserContext";
import { useNavigate } from "react-router-dom";
import "./SignUp.css";
import { Link } from "react-router-dom";

export default function SignUp() {
  const [nombreCompleto, setNombreCompleto] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rol,setRol]= useState("");
  const [user, setUser] = useState([]);
  const navigate = useNavigate();

  const checkUser = (nombre, apellido, email, password, confirmPassword,checkbox1) => {
    if (password == confirmPassword) {
      setPassword(document.getElementById("passwordConfirm").value);
      if (email.includes("@")) {
        setNombreCompleto(nombre + " " + apellido);
        setEmail(email);
        setPassword(password);
        NewUser(nombreCompleto, email, password,checkbox1);
        
      } else {
        alert("Porfavor Ingrese un email valido");
      }
    } else {
      alert("Error las contraseñas no coinciden");
    }
  };
  const NewUser = (nombre, email, password,checkbox1) => {    
    setUser({ mail: email, password: password, name: nombre });
  };
  return (
    <div className="App m-5border border-dark rounded p-5">
      <form id="formulario">
        <h3>Sign Up</h3>
        <div className="mb-3">
          <label>Nombre</label>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            id="Nombre"
            required
          />
        </div>
        <div className="mb-3">
          <label>Apellido</label>
          <input
            type="text"
            className="form-control"
            placeholder="Apellido"
            id="Apellido"
            required
          />
        </div>
        <div className="mb-3">
          <label>Email </label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            id="email"
            onChange={(e) => {
              if (e.target.value.includes("@")) {
                setEmail(e.target.value);
              } else {
                setEmail("");
              }
            }}
            required
          />
        </div>
        <div className="mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            id="password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            required
          />
        </div>
        <div className="mb-3">
          <label>Confirme su contraseña</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            id="passwordConfirm"
            required
          />
        </div>

        <div className="form-check form-switch d-flex justify-content-center px-5">
          <p className="pe-5 h5">Alumno</p>
          <label className="switch pb-3">            
            <input type="checkbox"  id="checkbox1" onChange={(e)=>{ }} />
            <span className="slider round"></span>
          </label>
          <p className="ps-5 h5">Profesor</p>
        </div>
        <div className="d-grid">
          <button
            type="submit"
            className="btn btn-primary"
            onClick={() => {
              checkUser(
                document.getElementById("Nombre").value,
                document.getElementById("Apellido").value,
                email,
                password,
                document.getElementById("passwordConfirm").value,
                document.getElementById("checkbox1").value
              );
            }}
          >
            Sign Up
          </button>
        </div>
      </form>
    </div>
  );
}
