import React, { useState } from "react";
import CardClaseProfe from "../../components/card/CardClaseProfe";
import Grid from "../../components/grid/Grid";
import NavBar from "../../components/navBar/NavBar";
import mock from "../../data/mock.json"

export default function misClases() {
    const { user, setUser } = useUserContext();
    const [clasesFiltradas,setClasesFiltradas]=useState();
  return (
    <div>
      <NavBar />
      <Grid>
        <>
        {user.role=="Profesor"}?({
            setClasesFiltradas(mock.clases.filter(fil=>fil.profesor==user.id))
        }):()
        </>
        {mock.clases.map((clase) => (

          <CardClaseProfe
            key={clase.id}
            id={clase.id}
            name={clase.nombreMateria}
            profesor1={clase.profesor}
            duracion={clase.duracion}
            frecuencia={clase.frecuencia}
            costo={clase.costo}
            calificacion={clase.calificacion}
            comentarios={clase.comentarios}
          />
        ))}
      </Grid>
    </div>
  );
}
