import React, { useContext, useState } from "react";
import mock from "../../data/mock.json";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../../contexts/UserContext";
import "./CardClase.css";
import NavBar from "../navBar/NavBar";

export default function CardClase({
  id,
  name,
  profesor,
  duracion,
  frecuencia,
  costo,
  calificacion,
  comentarios,
}) {
  const { user } = useContext(UserContext);
  const [details, setDetails] = useState(false);
  const redirect = () => {
    setDetails((current) => !current);
  };
  
  const Detalles = () => {
    return (
      
      <div>
        
        <p className="mt-2 fw-bold ">Calificacion: {calificacion}</p>        
        <div className="text-start">
            <p className="p-0">Comentarios:</p>            
                {comentarios.map(comentario=>(
                  <div className="text-start mx-2 border border-warning rounded">
                    <p key={"Alumno"+comentario.id} className="p-1 my-0 fw-bold">Alumno: {comentario.alumno}</p>
                    <hr className="m-1"/>
                    <p key={"comentario"+comentario.id} className="px-2 py-0 my-0">{comentario.comentario}</p>                 
                  </div>
                ))}
            
        </div>
      </div>
    );
  };
  return (
    <>
    <NavBar/>
    <div className="card">      
      <div className="container">
        <h4 className="display-6">
          <b>{name}</b>
        </h4>
        <h5>
          <b>{profesor}</b>
        </h5>
        <div>
          <p className="m-0">Frecuencia: {frecuencia}</p>
          <p className="m-0">Duracion: {duracion}</p>
          <p className="m-0">Costo: ${costo}</p>
        </div>
        <button onClick={redirect}>Ver detalles</button>
        {details && <Detalles />}
      </div>
    </div>
    </>
  );
}
