import React, {useContext, useState} from "react";
import mock from "../../data/mock.json";
import {UserContext} from "../../contexts/UserContext";
import {useNavigate} from "react-router-dom";
import "./SignUp.css";
import { Link } from "react-router-dom"; 

export default function SignUp() {
    const [nombreCompleto, setNombreCompleto] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const checkUser = (nombre,apellido,email,password,confirmPassword) =>{
      if(password == confirmPassword&&nombre!=''&&apellido!=''&&email!='') {
        setPassword(document.getElementById("passwordConfirm").value);              
        }else{
          alert("Error las contraseñas no coinciden");}
    }
    const NewUser=({nombre,email,password}) =>{

    }
    return <div className="App m-5border border-dark rounded p-5">
        <form id="formulario">
        <h3>Sign Up</h3>
        <div className="mb-3">
          <label>Nombre</label>
          <input
            type="text"
            className="form-control"
            placeholder="Nombre"
            id="Nombre"
          />
        </div>
        <div className="mb-3">
          <label>Apellido</label>
          <input type="text" className="form-control" placeholder="Apellido"  id="Apellido"/>
        </div>
        <div className="mb-3">
          <label>Email </label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            id="email"
            onChange={e=> {if((e.target.value).includes("@")){
              setEmail(e.target.value);
            }else{setEmail('');}
            }}
          />
        </div>
        <div className="mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            id="password"
            onChange={e=> {setPassword(e.target.value);}}
          />
        </div>
        <div className="mb-3">
          <label>Confirme su contraseña</label>
          <input
            type="password"
            className="form-control"
            placeholder="Password"
            id="passwordConfirm"
          />
        </div>
        <div className="d-grid">
          <button type="submit" className="btn btn-primary" onClick={()=>{
                         
            checkUser(document.getElementById("Nombre").value,document.getElementById("Apellido").value,email,password,document.getElementById("passwordConfirm").value);
            
          }}>
            Sign Up
          </button>
        </div>
        <p className="forgot-password text-right">
          Already registered <a href="#">sign in?</a>
        </p>
      </form>
        


    </div>
}
