import React, { useState } from "react";
import { useUserContext } from "../../contexts/UserContext";
import "./CardClase.css";
import mock from "../../data/mock.json";

export default function CardClaseFilter({
  id,
  name,
  profesor1,
  duracion,
  frecuencia,
  costo,
  calificacion,
  comentarios,
  _alumnos,
}) {
  const { user, profesores, alumnos } = useUserContext();
  const clases = mock.clases;
  const [combobox, setCombobox] = useState(false);
  const [filterClase, setFilterClase] = useState({});
  const redirect = () => {
    setCombobox((current) => !current);
  };

  const Combobox = (val) => {
    const variable = val.value;
    return (
      <>
        <select name="select" id={"select" + variable}>
          <option selected="selected" defaultValue="Pendiente">
            Pendiente
          </option>
          <option value="Aceptado">Aceptado</option>
          <option value="Finalizada">Finalizada</option>
          <option value="Rechazado">Rechazado</option>
        </select>
        <button
          id={variable}
          className="btn btn-success"
          onClick={() => guardarEstado(val)}
        >
          Guardar
        </button>
      </>
    );
  };

  const guardarEstado = (val1) => {
    const variable = val1.value;
    const selects = document.getElementById("select" + variable);
    document.getElementById("estado" + variable).innerHTML = selects.value;
    return setCombobox((current) => !current);
  };
  const controlComents = () => {
    return (
      <>
        <h3>Comentarios:</h3>
        {comentarios.map((comentario) => (
          <div className="text-start mx-2 border border-warning rounded">
            <p key={"Alumno" + comentario.id} className="p-1 my-0 fw-bold">
              Alumno: {comentario.alumno}
            </p>
            <hr className="m-1" />
            <p key={"comentario" + comentario.id} className="px-2 py-0 my-0 fst-italic fst-bold" >
              {comentario.comentario}
            </p>
            <div key={"estado"} className="px-2 py-0 my-0">
              Estado:
              <p id={"estado" + comentario.id}>{comentario.estado}</p>
              {combobox && <Combobox value={comentario.id}></Combobox>}
            </div>
          </div>
        ))}
      </>
    );
  };

  if (user.role === "Profesor") {
    if (profesor1 == user.id) {
      return (
        <div key={id} className="card w-50">
          <div className="container">
            <h4 className="display-4">
              <b>{name}</b>
            </h4>
            <section className="w-100 d-flex justify-content-end row ">
            <button className="btn btn-warning my-1" onClick={redirect}>
                Modificar Clase
              </button>
              <button className="btn btn-warning my-1" onClick={redirect}>
                Modificar Comentarios
              </button>
            </section>
            <div className="d-flex justify-content-center">
              <p className="mx-2 border border-warning p-2">
                Frecuencia: {frecuencia}
              </p>

              <p className="mx-2 border border-warning p-2">
                Duracion: {duracion}
              </p>
            </div>
            <div>{controlComents()}</div>
          </div>
        </div>
      );
    }
  } else {
  }
}
