import React, {useContext, useState} from "react";
import {useNavigate} from "react-router-dom";
import {UserContext} from "../../contexts/UserContext";
import "./Card.css";

export default function CardClase({id, name, profesor,duracion,frecuencia,costo,calificacion,comentarios}) {
    const {user} = useContext(UserContext);
    const [details, setDetails]= useState(false);
    const redirect = () => {
        setDetails(current => !current);
    }
    const Detalles =()=>{
        return (
            <div>
                <h2>box</h2>
            </div>
        )
    }
    return (<div className="card">
            <img src={require("../../assets/img_avatar.png")} alt="Avatar" style={{width: 300, height: 300}}/>
            <div className="container">
                <h4 className="display-6"><b>{name}</b></h4>
                <h5><b>{profesor}</b></h5>            
                <div >
                <p className="m-0">Frecuencia: {frecuencia}</p>
                <p className="m-0">Duracion: {duracion}</p>
                <p className="m-0">Costo: ${costo}</p>
                </div>
                <button onClick={redirect}>Ver detalles</button>
                {details&&(<div><h2>Buenas</h2></div>)}
                {details&&(<Detalles/>)}
            </div>
        </div>
    )
}
